$("#header").load("header.html",function(){if(sessionStorage.loginUname===undefined){$("#welcome").html("您好，欢迎来到本网站")}else{$("#welcome").html("欢迎回来："+sessionStorage.loginUname)}});$("#uname").blur(function(){var a=$("#uname").val();var b=/^[0-9a-zA-Z]{3,10}$/;if(!b.test(a)){$("#uname").css("borderColor","#DD514C")}else{$("#uname").css("borderColor","#5EB95E")}});$("#upwd1").blur(function(){var a=$("#upwd1").val();var b=/^[0-9a-zA-Z]{6,12}$/;if(!b.test(a)){$("#upwd1").css("borderColor","#DD514C")}else{$("#upwd1").css("borderColor","#5EB95E")}});$("#upwd2").blur(function(){var b=$("#upwd1").val();var a=$("#upwd2").val();if(a!==""&&a===b){$("#upwd2").css("borderColor","#5EB95E")}else{$("#upwd2").css("borderColor","#DD514C")}});$("#bt-register").click(function(){$.ajax({type:"POST",url:"/user/register",data:{uname:$("#uname").val(),upwd:$("#upwd1").val()},success:function(a){if(a.code===200){alert("注册成功！3s跳转到登录页面...");setTimeout(function(){location.href="login.html"},3000)}else{alert("注册失败！错误消息："+a.msg)}}})});